// Function node in n8n
// Parse AI output and return a single item object

const raw = $json.output;

let resume;
try {
  resume = JSON.parse(raw);
} catch (e) {
  throw new Error("Failed to parse resume JSON: " + e.message);
}

// ✅ Return ONE object with a `json` key
return {
  json: resume
};

// Get all items from the previous node
const items = $input.all();
const contacts = items.map(item => item.json);

if (!contacts || contacts.length === 0) {
  return { error: 'No email data found', contacts: [] };
}

// Priority departments for sales/outreach
const priorityDepartments = ['executive', 'management', 'sales', 'marketing'];

// Score and sort contacts
const scoredContacts = contacts.map(contact => {
  let score = 0;

  // Department priority
  if (priorityDepartments.includes(contact.department)) {
    score += 10;
  }

  // Confidence score
  score += contact.confidence / 10;

  // Executive seniority bonus
  if (contact.seniority === 'executive') {
    score += 5;
  }

  return { ...contact, score };
}).sort((a, b) => b.score - a.score);

const bestContact = scoredContacts[0];

// Extract domain from first email to get company name
const domain = bestContact.value.split('@')[1];

// Return only the best contact
return {
  recipient: {
    fullName: `${bestContact.first_name} ${bestContact.last_name}`,
    firstName: bestContact.first_name,
    lastName: bestContact.last_name,
    position: bestContact.position_raw || bestContact.position,
    department: bestContact.department,
    seniority: bestContact.seniority,
    email: bestContact.value
  },
  company: {
    domain: domain,
    organization: domain.split('.')[0]
  },
  confidence: bestContact.confidence,
  verificationStatus: bestContact.verification.status,
  score: bestContact.score
};

// Get the raw text from model output
const rawText = $input.first().json.content.parts[0].text

// Remove ```json and ``` if present
const cleaned = rawText.replace(/```json|```/g, '').trim();

// Parse JSON
let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (e) {
  throw new Error("Failed to parse LLM output as JSON: " + e.message);
}

return [
  {
    json: {
      email_subject: parsed.email_subject || "",
      email_body: parsed.email_body || ""
    }
  }
];

return [
  {
    json: {
      status: "success",
      ai_application_id: $('Webhook').first().json.body.ai_application_id,   
      client_id: $('Webhook').first().json.body.client_id,                   
      job_id: $('Webhook').first().json.body.job_id,                      
      ai_generated_content: {
        email_subject: $('cleanCoverLetter').first().json.email_subject,         
        email_body: $('cleanCoverLetter').first().json.email_body,             
        resume_content: {
          blob_url:  $('saveCVToVercel').first().json.url           
        },
      metadata: {
        resume_blob_url: $('saveCVToVercel').first().json.url
      }
    },
        discovery: {
          primary_email: $('SortedEmail').first().json.recipient.email
        } 
      }
  }
];

